import random

class Karakter:
    def __init__(self, ad, can, saldiri_gucu):
        self.ad = ad
        self.can = can
        self.saldiri_gucu = saldiri_gucu

    def saldir(self, rakip):
        vurus = random.randint(self.saldiri_gucu // 2, self.saldiri_gucu)
        print(f"{self.ad}, {rakip.ad}'a {vurus} hasar verdi!")
        rakip.can -= vurus
        if rakip.can < 0:
            rakip.can = 0
        print(f"{rakip.ad}'ın canı: {rakip.can}")

    def ozel_yetenek(self, rakip):
        # Bu metot, alt sınıflarda (Savaşçı, Büyücü) farklı şekillerde uygulanacak.
        pass

class Savasci(Karakter):
    def __init__(self, ad, can, saldiri_gucu):
        super().__init__(ad, can, saldiri_gucu)
        self.ozel_yetenek_kullanildi = False

    def ozel_yetenek(self, rakip):
        if not self.ozel_yetenek_kullanildi:
            print("Savaşçı özel yeteneğini kullanıyor: 'Kudretli Vuruş'!")
            vurus = self.saldiri_gucu * 2  # İki katı hasar
            print(f"{self.ad}, {rakip.ad}'a {vurus} hasar verdi!")
            rakip.can -= vurus
            if rakip.can < 0:
                rakip.can = 0
            print(f"{rakip.ad}'ın canı: {rakip.can}")
            self.ozel_yetenek_kullanildi = True
        else:
            print("Özel yetenek zaten kullanıldı!")

class Buyucu(Karakter):
    def __init__(self, ad, can, saldiri_gucu):
        super().__init__(ad, can, saldiri_gucu)
        self.iyilestirme_miktari = 20

    def ozel_yetenek(self):
        print("Büyücü özel yeteneğini kullanıyor: 'Şifa'!")
        self.can += self.iyilestirme_miktari
        print(f"{self.ad} iyileşti. Canı: {self.can}")

def oyun_sonu_kontrol(karakter1, karakter2):
    if karakter1.can <= 0:
        print(f"{karakter2.ad} kazandı!")
        return True
    elif karakter2.can <= 0:
        print(f"{karakter1.ad} kazandı!")
        return True
    return False

# Karakterleri oluştur
karakter1 = Savasci("Savaşçı", 100, 20)
karakter2 = Buyucu("Büyücü", 80, 30)

# Oyuncunun karakter seçimi
print("Karakterinizi seçin:")
print("1. Savaşçı")
print("2. Büyücü")
secim = input("Seçiminiz (1 veya 2): ")

if secim == "1":
    oyuncu = karakter1
    bilgisayar = karakter2
elif secim == "2":
    oyuncu = karakter2
    bilgisayar = karakter1
else:
    print("Geçersiz seçim. Savaşçı seçildi.")
    oyuncu = karakter1
    bilgisayar = karakter2

# Oyun döngüsü
while True:
    # Oyuncunun sırası
    print("\nSıra sende!")
    print("1. Saldır")
    print("2. Özel Yetenek")
    secim = input("Seçiminiz (1 veya 2): ")

    if secim == "1":
        oyuncu.saldir(bilgisayar)
    elif secim == "2":
        if isinstance(oyuncu, Savasci):
            oyuncu.ozel_yetenek(bilgisayar)
        elif isinstance(oyuncu, Buyucu):
            oyuncu.ozel_yetenek()
    else:
        print("Geçersiz seçim!")
        continue

    if oyun_sonu_kontrol(oyuncu, bilgisayar):
        break

    # Bilgisayarın sırası
    print("\nBilgisayarın sırası!")
    # Bilgisayarın stratejisi (basit):
    if bilgisayar.can < 30 and isinstance(bilgisayar, Buyucu):
        print("Bilgisayar şifa kullanıyor!")
        bilgisayar.ozel_yetenek()
    else:
        print("Bilgisayar saldırıyor!")
        bilgisayar.saldir(oyuncu)

    if oyun_sonu_kontrol(oyuncu, bilgisayar):
        break

