class Oyuncu:
    # ... (önceki kod)

    def ozel_saldiri(self, rakip):
        if self.isim == "Ryu":
            vurus_gucu = self.saldiri_gucu * 2 + random.randint(0, 10)  # Daha güçlü bir saldırı
            print("HADOUKEN!")
        elif self.isim == "Ken":
            vurus_gucu = self.saldiri_gucu * 1.5 + random.randint(0, 7)
            print("SHORYUKEN!")
        else:
            vurus_gucu = self.saldiri_gucu + random.randint(0, 5)

        rakip.can -= vurus_gucu
        print(f"{self.isim}, {rakip.isim}'e {vurus_gucu} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.isim} yenildi!")
        return rakip.can <= 0

