import random

def tahmin_oyunu():
  print("Merhaba! Sayı Tahmin Oyununa hoş geldiniz!")
  zorluk_seviyesi = input("Zorluk seviyesi seçin (Kolay/Orta/Zor): ").lower()

  if zorluk_seviyesi == "kolay":
    sayi_araligi = range(1, 51)
  elif zorluk_seviyesi == "orta":
    sayi_araligi = range(51, 101)
  elif zorluk_seviyesi == "zor":
    sayi_araligi = range(1, 101)
  else:
    print("Geçersiz zorluk seviyesi. Kolay seçeneği varsayıyorum.")
    sayi_araligi = range(1, 51)

  sayi = random.choice(sayi_araligi)
  tahmin_sayisi = 0
  hak_sayisi = 7
  skor = 0

  print(f"0 ile {sayi_araligi[-1]} arasında bir sayı düşündüm. Tahmin etmeye başla!")

  while hak_sayisi > 0:
    try:
      tahmin_sayisi = int(input("Tahmininiz: "))
    except ValueError:
      print("Lütfen bir sayı girin.")
      continue

    if tahmin_sayisi < sayi:
      print("Daha büyük bir sayı!")
    elif tahmin_sayısı > sayi:
      print("Daha küçük bir sayı!")
    else:
      print(f"Tebrikler! {sayi} sayısını {7 - hak_sayısı} hakkta buldunuz!")
      skor = 7 - hak_sayısı
      break

    hak_sayisi -= 1
    print(f"Kalan haklarınız: {hak_sayısı}")

  if hak_sayısı == 0:
    print(f"Üzgünüm, {sayi} sayısını bulamadınız.")

  print(f"Toplam tahmin sayısı: {7 - hak_sayısı}")
  print(f"Skorunuz: {skor}")

  tekrar_oyna = input("Tekrar oynamak ister misiniz? (evet/hayır): ").lower()
  if tekrar_oyna == "evet":
    tahmin_oyunu()

tahmin_oyunu()

