def oyun_dongusu(oyuncu1, oyuncu2):
    while True:
        # Oyuncu 1'in sırası
        print("\n" + oyuncu1.isim + "'in sırası:")
        secim = input("Saldır (s) veya Savun (v)? ").lower()
        if secim == 's':
            if oyuncu1.saldir(oyuncu2):
                break  # Oyuncu 2 yenildi, döngüden çık
        elif secim == 'v':
            oyuncu1.savun()
        else:
            print("Geçersiz seçim!")

        oyuncu1.durum()
        oyuncu2.durum()

        # Oyuncu 2'nin sırası (benzer şekilde)
        print("\n" + oyuncu2.isim + "'in sırası:")
        secim = input("Saldır (s) veya Savun (v)? ").lower()
        if secim == 's':
            if oyuncu2.saldir(oyuncu1):
                break  # Oyuncu 1 yenildi, döngüden çık
        elif secim == 'v':
            oyuncu2.savun()
        else:
            print("Geçersiz seçim!")

        oyuncu1.durum()
        oyuncu2.durum()

# Oyuncuları oluşturalım
oyuncu1 = Oyuncu("Ryu")
oyuncu2 = Oyuncu("Ken")

# Oyunu başlatalım
oyun_dongusu(oyuncu1, oyuncu2)

