import random

class Karakter:
    def __init__(self, ad, can=100, guc=10):
        self.ad = ad
        self.can = can
        self.guc = guc

    def saldir(self, rakip):
        vurus = random.randint(0, self.guc)
        rakip.can -= vurus
        print(f"{self.ad}, {rakip.ad}'a {vurus} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.ad} yenildi!")
            return True
        return False

    def ozel_saldiri(self, rakip):
        # Özel saldırı daha fazla hasar verir
        vurus = random.randint(self.guc // 2, self.guc * 2)
        rakip.can -= vurus
        print(f"{self.ad}, {rakip.ad}'a ÖZEL SALDIRI ile {vurus} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.ad} yenildi!")
            return True
        return False

# Karakterleri oluştur
oyuncu1 = Karakter("Ryu")
oyuncu2 = Karakter("Ken")

# Oyun döngüsü
while True:
    # Oyuncu 1'in hamlesi (kullanıcıdan al)
    print(f"{oyuncu1.ad} hamle seçin: (1) Saldır, (2) Özel Saldırı")
    hamle = input("> ")

    if hamle == "1":
        if oyuncu1.saldir(oyuncu2):
            break
    elif hamle == "2":
        if oyuncu1.ozel_saldiri(oyuncu2):
            break
    else:
        print("Geçersiz hamle!")
        continue

    # Oyuncu 2'nin hamlesi (basit AI)
    hamle = random.choice(["1", "2"])
    print(f"{oyuncu2.ad} hamle yaptı: {hamle}")

    if hamle == "1":
        if oyuncu2.saldir(oyuncu1):
            break
    elif hamle == "2":
        if oyuncu2.ozel_saldiri(oyuncu1):
            break

    print(f"{oyuncu1.ad} Can: {oyuncu1.can}, {oyuncu2.ad} Can: {oyuncu2.can}")

