import random

def sayi_tahmin_oyunu():
    """
    Rastgele bir sayı üretir ve kullanıcının bu sayıyı tahmin etmesini sağlar.
    """

    rastgele_sayi = random.randint(1, 100)  # 1 ile 100 arasında rastgele bir sayı seç
    tahmin_sayisi = 0

    print("1 ile 100 arasında bir sayı tahmin etmeye çalış!")

    while True:
        try:
            tahmin = int(input("Tahminini gir: "))
            tahmin_sayisi += 1

            if tahmin < rastgele_sayi:
                print("Daha yüksek bir sayı tahmin et.")
            elif tahmin > rastgele_sayi:
                print("Daha düşük bir sayı tahmin et.")
            else:
                print(f"Tebrikler! {rastgele_sayi}'yi {tahmin_sayisi} tahminde buldun.")
                break
        except ValueError:
            print("Geçersiz giriş. Lütfen bir sayı gir.")

# Oyunu başlat
if __name__ == "__main__":
    sayi_tahmin_oyunu()

