import random

class Karakter:
    def __init__(self, ad, can, saldiri_gucu):
        self.ad = ad
        self.can = can
        self.saldiri_gucu = saldiri_gucu
        self.iyilesme_sayisi = 2  # İyileşme sayısını sınırlayalım

    def saldir(self, rakip):
        vurus = random.randint(self.saldiri_gucu // 2, self.saldiri_gucu)
        print(f"{self.ad}, {rakip.ad}'e {vurus} hasar verdi!")
        rakip.can -= vurus
        if rakip.can < 0:
            rakip.can = 0
        print(f"{rakip.ad}'in canı: {rakip.can}")

    def iyiles(self):
        if self.iyilesme_sayisi > 0:
            iyilesme_miktari = random.randint(10, 20)
            self.can += iyilesme_miktari
            print(f"{self.ad}, {iyilesme_miktari} can iyileştirdi. Yeni canı: {self.can}")
            self.iyilesme_sayisi -= 1
        else:
            print(f"{self.ad}'in iyileşme hakkı kalmadı!")

    def hayatta_mi(self):
        return self.can > 0

oyuncu = Karakter("Oyuncu", 100, 20)
bilgisayar = Karakter("Bilgisayar", 100, 15)

print("Dövüş Başlıyor!")

while oyuncu.hayatta_mi() and bilgisayar.hayatta_mi():
    print("\n--- Oyuncu Sırası ---")
    print("1. Saldır")
    print("2. İyileş")
    secim = input("Hamleni seç (1 veya 2): ")

    if secim == "1":
        oyuncu.saldir(bilgisayar)
        if not bilgisayar.hayatta_mi():
            print("Oyuncu kazandı!")
            break
    elif secim == "2":
        oyuncu.iyiles()
    else:
        print("Geçersiz hamle!")
        continue

    print("\n--- Bilgisayar Sırası ---")
    if bilgisayar.hayatta_mi():
        bilgisayar.saldir(oyuncu)
        if not oyuncu.hayatta_mi():
            print("Bilgisayar kazandı!")
            break

