import random

class Karakter:
    def __init__(self, ad, can=100, saldiri_gucu=10):
        self.ad = ad
        self.can = can
        self.saldiri_gucu = saldiri_gucu

    def saldir(self, hedef):
        zarar = random.randint(0, self.saldiri_gucu)
        hedef.can -= zarar
        print(f"{self.ad}, {hedef.ad}'a {zarar} hasar verdi! Kalan can: {hedef.can}")
        if hedef.can <= 0:
            print(f"{hedef.ad} yenildi!")
        return hedef.can <= 0  # Eğer hedef yenildiyse True döner

def oyun_dongusu(karakter1, karakter2):
    while True:
        print(f"\n{karakter1.ad} ({karakter1.can} can) vs. {karakter2.ad} ({karakter2.can} can)")
        komut = input(f"{karakter1.ad}, saldır (s) veya bekle (b)? ").lower()

        if komut == "s":
            if karakter1.saldir(karakter2):
                break  # Karakter2 yenildiyse döngüden çık
        elif komut == "b":
            print(f"{karakter1.ad} bekliyor...")
        else:
            print("Geçersiz komut!")
            continue  # Geçersiz komut girildiyse tekrar komut iste

        if karakter2.can <= 0:
            break  # Karakter2 zaten yenildiyse döngüden çık

        # Sıra Karakter2'ye geçiyor (otomatik saldırı)
        if karakter2.saldir(karakter1):
            break  # Karakter1 yenildiyse döngüden çık

if __name__ == "__main__":
    karakter1 = Karakter("Ryu")
    karakter2 = Karakter("Ken")

    print("Dövüş Oyunu Başlıyor!")
    oyun_dongusu(karakter1, karakter2)
    print("Oyun Bitti!")

