import random

def sayi_tahmin_oyunu():
    """Sayı Tahmin Oyunu'nun ana fonksiyonu."""

    rastgele_sayi = random.randint(1, 100) # 1 ile 100 arasında rastgele bir sayı tut
    tahmin_sayisi = 0 # Kaç kere tahmin ettiğimizi saymak için

    print("Sayı Tahmin Oyununa Hoş Geldin!")
    print("1 ile 100 arasında bir sayı tuttum. Bakalım tahmin edebilecek misin?")

    while True: # Sonsuz döngü (doğru tahmin yapana kadar devam edecek)
        try:
            tahmin = int(input("Tahminini gir: ")) # Kullanıcıdan tahmin al
            tahmin_sayisi += 1 # Tahmin sayısını 1 artır
        except ValueError:
            print("Geçersiz giriş. Lütfen bir sayı gir.")
            continue # Döngünün başına dön

        if tahmin < rastgele_sayi:
            print("Daha yüksek bir sayı tahmin et.")
        elif tahmin > rastgele_sayi:
            print("Daha düşük bir sayı tahmin et.")
        else:
            print(f"Tebrikler! {rastgele_sayi} sayısını {tahmin_sayisi} tahminde buldun.")
            break # Döngüyü sonlandır

if __name__ == "__main__":
    sayi_tahmin_oyunu() # Oyunu başlat

