import random

class Oyuncu:
    def __init__(self, isim, can=100, saldiri_gucu=10):
        self.isim = isim
        self.can = can
        self.saldiri_gucu = saldiri_gucu

    def saldir(self, rakip):
        vurus_gucu = self.saldiri_gucu + random.randint(0, 5)  # Rastgelelik ekleyelim
        rakip.can -= vurus_gucu
        print(f"{self.isim}, {rakip.isim}'e {vurus_gucu} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.isim} yenildi!")
        return rakip.can <= 0

    def savun(self):
        print(f"{self.isim} savunmaya geçti!")
        # Savunma mekaniği eklenebilir (örneğin, sonraki saldırıdan daha az hasar almak)

    def durum(self):
        print(f"{self.isim}: Can = {self.can}")

