# Klavye girdilerini işle
    if event.type == pygame.KEYDOWN:
        if event.key == pygame.K_a:
            oyuncu1.rect.x -= oyuncu1.hiz
        if event.key == pygame.K_d:
            oyuncu1.rect.x += oyuncu1.hiz
        if event.key == pygame.K_w:  # Oyuncu 1 saldırı tuşu
            if pygame.sprite.collide_rect(oyuncu1, oyuncu2):
                oyuncu2.can -= 10
                print("Oyuncu 1 saldırdı! Oyuncu 2 canı:", oyuncu2.can)

        if event.key == pygame.K_LEFT:
            oyuncu2.rect.x -= oyuncu2.hiz
        if event.key == pygame.K_RIGHT:
            oyuncu2.rect.x += oyuncu2.hiz
        if event.key == pygame.K_UP:  # Oyuncu 2 saldırı tuşu
            if pygame.sprite.collide_rect(oyuncu2, oyuncu1):
                oyuncu1.can -= 10
                print("Oyuncu 2 saldırdı! Oyuncu 1 canı:", oyuncu1.can)

    # Can kontrolü
    if oyuncu1.can <= 0:
        print("Oyuncu 2 kazandı!")
        calisiyor = False
    if oyuncu2.can <= 0:
        print("Oyuncu 1 kazandı!")
        calisiyor = False

