import random

sayı = random.randint(1, 100)
tahmin_sayısı = 0
hak_sayısı = 7

print("0 ile 100 arasında bir sayı düşündüm. Tahmin etmeye başla!")

while hak_sayısı > 0:
  try:
    tahmin_sayısı = int(input("Tahmininiz: "))
  except ValueError:
    print("Lütfen bir sayı girin.")
    continue

  if tahmin_sayısı < sayı:
    print("Daha büyük bir sayı!")
  elif tahmin_sayısı > sayı:
    print("Daha küçük bir sayı!")
  else:
    print(f"Tebrikler! {sayı} sayısını {7 - hak_sayısı} hakkta buldunuz!")
    break

  hak_sayısı -= 1
  print(f"Kalan haklarınız: {hak_sayısı}")

if hak_sayısı == 0:
  print(f"Üzgünüm, {sayı} sayısını bulamadınız.")

