import random

class Karakter:
    def __init__(self, isim, can=100, guc=10, ozel_yetenek_adi="Yok", ozel_yetenek_gucu=0):
        self.isim = isim
        self.can = can
        self.guc = guc
        self.ozel_yetenek_adi = ozel_yetenek_adi
        self.ozel_yetenek_gucu = ozel_yetenek_gucu

    def saldir(self, rakip):
        vurus = random.randint(0, self.guc)
        rakip.can -= vurus
        print(f"{self.isim} {rakip.isim}'e {vurus} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.isim} yenildi!")
        return rakip.can <= 0

    def ozel_saldiri(self, rakip):
        if self.ozel_yetenek_adi == "Yok":
            print(f"{self.isim} özel bir yeteneğe sahip değil!")
            return False

        vurus = random.randint(0, self.ozel_yetenek_gucu)
        rakip.can -= vurus
        print(f"{self.isim} {rakip.isim}'e {self.ozel_yetenek_adi} ile {vurus} hasar verdi!")
        if rakip.can <= 0:
            print(f"{rakip.isim} yenildi!")
        return rakip.can <= 0

def karakter_sec():
    isim = input("Karakterinizin adını girin: ")
    ozel_yetenek_adi = input("Özel yeteneğinizin adını girin (Yok ise 'Yok' yazın): ")
    ozel_yetenek_gucu = 0
    if ozel_yetenek_adi != "Yok":
        ozel_yetenek_gucu = int(input("Özel yeteneğinizin gücünü girin: "))
    return Karakter(isim, ozel_yetenek_adi=ozel_yetenek_adi, ozel_yetenek_gucu=ozel_yetenek_gucu)

def bilgisayar_hamlesi(bilgisayar, rakip):
    if bilgisayar.ozel_yetenek_adi != "Yok" and bilgisayar.can < 30:
        print("Bilgisayar özel yeteneğini kullanıyor!")
        return bilgisayar.ozel_saldiri(rakip)
    else:
        return bilgisayar.saldir(rakip)

def oyun_döngüsü(oyuncu1, oyuncu2):
    while True:
        print(f"{oyuncu1.isim}: Can = {oyuncu1.can}, {oyuncu2.isim}: Can = {oyuncu2.can}")

        # Oyuncu 1'in saldırısı
        secim = input("Saldırmak için 's', özel saldırı için 'o' tuşuna basın: ")
        if secim.lower() == 's':
            if oyuncu1.saldir(oyuncu2):
                print(f"Kazanan: {oyuncu1.isim}!")
                break
        elif secim.lower() == 'o':
            if oyuncu1.ozel_saldiri(oyuncu2):
                print(f"Kazanan: {oyuncu1.isim}!")
                break
        else:
            print("Geçersiz seçim!")
            continue

        if oyuncu2.can <= 0:
            print(f"Kazanan: {oyuncu1.isim}!")
            break

        # Oyuncu 2'nin saldırısı (Bilgisayar)
        if bilgisayar_hamlesi(oyuncu2, oyuncu1):
            print(f"Kazanan: {oyuncu2.isim}!")
            break

        if oyuncu1.can <= 0:
            print(f"Kazanan: {oyuncu2.isim}!")
            break

# Oyuncu karakterini oluştur
oyuncu1 = karakter_sec()
oyuncu2 = Karakter("Bilgisayar", 80, 12, "Süper Vuruş", 25) # Bilgisayar karakteri

# Oyunu başlat
oyun_döngüsü(oyuncu1, oyuncu2)

