class Karakter:
    def __init__(self, ad, can, saldiri_gucu):
        self.ad = ad
        self.can = can
        self.saldiri_gucu = saldiri_gucu
        self.iyilesme_sayisi = 2  # İyileşme sayısını sınırlayalım

    def saldir(self, rakip):
        vurus = random.randint(self.saldiri_gucu // 2, self.saldiri_gucu)
        print(f"{self.ad}, {rakip.ad}'e {vurus} hasar verdi!")
        rakip.can -= vurus
        if rakip.can < 0:
            rakip.can = 0
        print(f"{rakip.ad}'in canı: {rakip.can}")

    def iyiles(self):
        if self.iyilesme_sayisi > 0:
            iyilesme_miktari = random.randint(10, 20)
            self.can += iyilesme_miktari
            print(f"{self.ad}, {iyilesme_miktari} can iyileştirdi. Yeni canı: {self.can}")
            self.iyilesme_sayisi -= 1
        else:
            print(f"{self.ad}'in iyileşme hakkı kalmadı!")

    def hayatta_mi(self):
        return self.can > 0

