import random

kelimeler = ["elma", "armut", "muz", "çilek", "karpuz"]
kelime = random.choice(kelimeler)
gizli_kelime = ["_"] * len(kelime)

haklar = 6

while haklar > 0:
  print(" ".join(gizli_kelime))
  tahmin = input("Bir harf tahmin edin: ").lower()

  if tahmin in kelime:
    for i in range(len(kelime)):
      if kelime[i] == tahmin:
        gizli_kelime[i] = tahmin
  else:
    haklar -= 1
    print("Yanlış tahmin! Kalan haklar:", haklar)

  if "_" not in gizli_kelime:
    print("Tebrikler, kelimeyi bildin! Kelime:", kelime)
    break

if haklar == 0:
  print("Maalesef kelimeyi bulamadın. Kelime:", kelime)

