import random

def oyuncu_yarat(isim):
    can = 100
    saldiri_gucu = random.randint(10, 20)
    return {"isim": isim, "can": can, "saldiri_gucu": saldiri_gucu}

def saldir(saldiran, savunulan):
    vurus_gucu = saldiran["saldiri_gucu"] + random.randint(-5, 5)  # Biraz rastgelelik
    savunulan["can"] -= vurus_gucu
    print(f"{saldiran['isim']} saldırdı! {savunulan['isim']} {vurus_gucu} hasar aldı.")

def oyun_dongusu():
    oyuncu1 = oyuncu_yarat(input("1. Oyuncunun adını girin: "))
    oyuncu2 = oyuncu_yarat(input("2. Oyuncunun adını girin: "))

    sira = random.choice([oyuncu1, oyuncu2])  # Rastgele başlama

    while oyuncu1["can"] > 0 and oyuncu2["can"] > 0:
        print(f"\n{sira['isim']}'nın sırası!")
        if sira == oyuncu1:
            saldir(oyuncu1, oyuncu2)
            sira = oyuncu2
        else:
            saldir(oyuncu2, oyuncu1)
            sira = oyuncu1

        print(f"{oyuncu1['isim']} Can: {oyuncu1['can']}")
        print(f"{oyuncu2['isim']} Can: {oyuncu2['can']}")

    if oyuncu1["can"] <= 0:
        print(f"\n{oyuncu2['isim']} KAZANDI!")
    else:
        print(f"\n{oyuncu1['isim']} KAZANDI!")

# Oyunu başlat
oyun_dongusu()

