def hesap_makinesi():
    """
    Basit bir komut satırı hesap makinesi.
    """
    print("Hesap Makinesi")
    print("Yapılabilecek işlemler: toplama (+), çıkarma (-), çarpma (*), bölme (/)")

    try:
        sayi1 = float(input("İlk sayıyı girin: "))
        sayi2 = float(input("İkinci sayıyı girin: "))
        islem = input("Yapmak istediğiniz işlemi girin (+, -, *, /): ")

        if islem == '+':
            sonuc = sayi1 + sayi2
        elif islem == '-':
            sonuc = sayi1 - sayi2
        elif islem == '*':
            sonuc = sayi1 * sayi2
        elif islem == '/':
            if sayi2 == 0:
                print("Hata: Sıfıra bölme yapılamaz!")
                return  # Fonksiyonu sonlandır
            sonuc = sayi1 / sayi2
        else:
            print("Hata: Geçersiz işlem!")
            return  # Fonksiyonu sonlandır

        print("Sonuç:", sonuc)

    except ValueError:
        print("Hata: Geçersiz sayı girişi!")
    except Exception as e:
        print("Beklenmeyen bir hata oluştu:", e)

# Hesap makinesini çalıştır
hesap_makinesi()

